(function(){tinymce.PluginManager.requireLangPack('styledtext');tinymce.create('tinymce.plugins.styledtextPlugin',{init:function(ed,url){ed.addCommand('mcestyledtext',function(){ed.windowManager.open({file:url+'/dialog.htm',width:320+parseInt(ed.getLang('styledtext.delta_width',0)),height:120+parseInt(ed.getLang('styledtext.delta_height',0)),inline:1},{plugin_url:url,some_custom_arg:'custom arg'});});ed.addButton('styledtext',{title:'styledtext.desc',cmd:'mcestyledtext',image:url+'/img/styledtext.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('styledtext',n.nodeName=='IMG');});},createControl:function(n,cm){return null;},getInfo:function(){return{longname:'styledtext plugin',author:'Some author',authorurl:'http://tinymce.moxiecode.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/styledtext',version:"1.0"};}});tinymce.PluginManager.add('styledtext',tinymce.plugins.styledtextPlugin);})();